function remove(n){swal({title:"Sure to remove this spider?",type:"warning",showCancelButton:!0},function(){app.delete("/spider/"+n,function(){window.location.reload()},function(){swal("Error","Delete failed","error")})})}function run(n){swal({title:"Sure to run this spider?",type:"warning",showCancelButton:!0},function(){app.post("/spider/"+n+"/run",null,function(){window.location.reload()},null,function(n){swal("Error",n.message,"error")})})}$(function(){app.activeMenu("Spider")});